mapper.update:
UPDATE member
SET pw = #{pw}
WHERE id = #{id}
AND email = #{email}

UPDATE member
SET pw = #{newPw}, pwcktime = NULL
WHERE id = #{id}
AND pw = #{pw}
AND pwcktime IS NOT NULL
AND TIMESTAMPDIFF(MINUTE, pwcktime, NOW()) < 11

UPDATE member
SET pw = #{pw},
    pwcktime = #{pwcktime}
WHERE id = #{id}

mapper.select:
SELECT id, email
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET pw = #{newPw}
WHERE id = #{id}
AND pw = #{pw}

mapper.delete:
DELETE 
FROM member
WHERE id = #{id}
AND pw = #{pw}
